def sol(N, K):
    return ans[str(N)][str(K)]

def print_matrix(mat):
    for row in mat:
        print(" ".join([str(r) for r in row]))

T = int(input())
for t in range(T):
    N, K = [int(n) for n in input().split()]
    message, matrix = sol(N, K)
    print("Case #{}: {}".format(t+1, message))
    if message != "IMPOSSIBLE":
        print_matrix(matrix)